import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import ThemeToggle from './ThemeToggle';
import { setdarkMode } from '../redux/theme/themeSlice';

function Navbar() {
  const user = useSelector((state) => state.auth.user);
  const dispatch = useDispatch();
  const darkMode = useSelector((state) => state.theme.darkMode);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const toggleDarkMode = () => {
    dispatch(setdarkMode(!darkMode));
  };

  return (
    <header className="bg-white dark:bg-gray-800 shadow-md">
      <nav className="container mx-auto px-6 py-4">
        <div className="flex justify-between items-center">
          <div className="text-2xl font-bold text-orange-500">CommunityGo</div>
          <div className="flex items-center space-x-6">
            <div className="hidden md:flex space-x-6">
              <NavLink 
                to="/" 
                className={({ isActive }) => 
                  `text-gray-600 dark:text-gray-300 hover:text-orange-500 dark:hover:text-orange-400 transition duration-300 ${
                    isActive ? 'font-bold' : ''
                  }`
                }
                aria-current={({ isActive }) => isActive ? 'page' : undefined}
              >
                Home
              </NavLink>
              <NavLink 
                to="/how-it-works" 
                className={({ isActive }) => 
                  `text-gray-600 dark:text-gray-300 hover:text-orange-500 dark:hover:text-orange-400 transition duration-300 ${
                    isActive ? 'font-bold' : ''
                  }`
                }
                aria-current={({ isActive }) => isActive ? 'page' : undefined}
              >
                How It Works
              </NavLink>
              <NavLink 
                to="/about" 
                className={({ isActive }) => 
                  `text-gray-600 dark:text-gray-300 hover:text-orange-500 dark:hover:text-orange-400 transition duration-300 ${
                    isActive ? 'font-bold' : ''
                  }`
                }
                aria-current={({ isActive }) => isActive ? 'page' : undefined}
              >
                About Us
              </NavLink>
              <NavLink 
                to="/contact" 
                className={({ isActive }) => 
                  `text-gray-600 dark:text-gray-300 hover:text-orange-500 dark:hover:text-orange-400 transition duration-300 ${
                    isActive ? 'font-bold' : ''
                  }`
                }
                aria-current={({ isActive }) => isActive ? 'page' : undefined}
              >
                Contact
              </NavLink>
              {user ? (
                <NavLink 
                  to="/profile" 
                  className={({ isActive }) => 
                    `text-orange-500 hover:underline ${
                      isActive ? 'font-bold' : ''
                    }`
                  }
                  aria-current={({ isActive }) => isActive ? 'page' : undefined}
                >
                  {user.username}
                </NavLink>
              ) : (
                <>
                  <NavLink 
                    to="/register" 
                    className={({ isActive }) => 
                      `text-blue-500 hover:underline ${
                        isActive ? 'font-bold' : ''
                      }`
                    }
                    aria-current={({ isActive }) => isActive ? 'page' : undefined}
                  >
                    Signup
                  </NavLink>
                  <NavLink 
                    to="/login" 
                    className={({ isActive }) => 
                      `text-blue-500 hover:underline ${
                        isActive ? 'font-bold' : ''
                      }`
                    }
                    aria-current={({ isActive }) => isActive ? 'page' : undefined}
                  >
                    Login
                  </NavLink>
                </>
              )}
            </div>
            <ThemeToggle darkMode={darkMode} toggleDarkMode={toggleDarkMode} />
          </div>
        </div>
      </nav>
    </header>
  );
}

export default Navbar;
