import React, { useState } from 'react';
import { MapPin, Package } from 'lucide-react';

export default function DeliveryPage() {
  const [formData, setFormData] = useState({
    from_address: '',
    from_city: '',
    from_postal_code: '',
    to_address: '',
    to_city: '',
    to_postal_code: '',
    package_size: 'SM', // Default package size
    delivery_date: '',
    details: '', // Optional details
  });

  const packageSizes = [
    { value: 'SM', label: 'Small' },
    { value: 'MD', label: 'Medium' },
    { value: 'LG', label: 'Large' },
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting:', formData);

    try {
      const response = await fetch('/api/deliveries/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Delivery request submitted successfully!');
        setFormData({
          from_address: '',
          from_city: '',
          from_postal_code: '',
          to_address: '',
          to_city: '',
          to_postal_code: '',
          package_size: 'SM',
          delivery_date: '',
          details: '',
        });
      } else {
        alert('Failed to submit delivery request. Please try again.');
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow-md">
        <nav className="container mx-auto px-6 py-4">
          <div className="flex justify-between items-center">
            <div className="text-2xl font-bold text-orange-500">CommunityGo</div>
            <div className="hidden md:flex space-x-6">
              <a href="#" className="text-gray-600 hover:text-orange-500 transition duration-300">Home</a>
              <a href="#" className="text-gray-600 hover:text-orange-500 transition duration-300">Deliveries</a>
              <a href="#" className="text-gray-600 hover:text-orange-500 transition duration-300">About Us</a>
              <a href="#" className="text-gray-600 hover:text-orange-500 transition duration-300">Contact</a>
            </div>
          </div>
        </nav>
      </header>

      <main className="container mx-auto px-6 py-20">
        <h1 className="text-4xl font-bold text-center text-gray-800 mb-10">Create a Delivery Request</h1>
        <div className="bg-white shadow-lg rounded-lg p-8 max-w-3xl mx-auto">
          <form onSubmit={handleSubmit}>
            {/* From Address */}
            <div className="mb-6">
              <label htmlFor="from_address" className="block text-gray-800 font-semibold mb-2">From Address</label>
              <input
                type="text"
                id="from_address"
                name="from_address"
                placeholder="Enter pickup address"
                value={formData.from_address}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>

            {/* From City */}
            <div className="mb-6">
              <label htmlFor="from_city" className="block text-gray-800 font-semibold mb-2">From City</label>
              <input
                type="text"
                id="from_city"
                name="from_city"
                placeholder="Enter city"
                value={formData.from_city}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>

            {/* From Postal Code */}
            <div className="mb-6">
              <label htmlFor="from_postal_code" className="block text-gray-800 font-semibold mb-2">From Postal Code</label>
              <input
                type="text"
                id="from_postal_code"
                name="from_postal_code"
                placeholder="Enter postal code"
                value={formData.from_postal_code}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>

            {/* To Address */}
            <div className="mb-6">
              <label htmlFor="to_address" className="block text-gray-800 font-semibold mb-2">To Address</label>
              <input
                type="text"
                id="to_address"
                name="to_address"
                placeholder="Enter drop-off address"
                value={formData.to_address}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>

            {/* To City */}
            <div className="mb-6">
              <label htmlFor="to_city" className="block text-gray-800 font-semibold mb-2">To City</label>
              <input
                type="text"
                id="to_city"
                name="to_city"
                placeholder="Enter city"
                value={formData.to_city}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>

            {/* To Postal Code */}
            <div className="mb-6">
              <label htmlFor="to_postal_code" className="block text-gray-800 font-semibold mb-2">To Postal Code</label>
              <input
                type="text"
                id="to_postal_code"
                name="to_postal_code"
                placeholder="Enter postal code"
                value={formData.to_postal_code}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>

            {/* Other Fields */}
            <div className="mb-6">
              <label htmlFor="package_size" className="block text-gray-800 font-semibold mb-2">Package Size</label>
              <select
                id="package_size"
                name="package_size"
                value={formData.package_size}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              >
                {packageSizes.map((size) => (
                  <option key={size.value} value={size.value}>{size.label}</option>
                ))}
              </select>
            </div>

            <div className="mb-6">
              <label htmlFor="delivery_date" className="block text-gray-800 font-semibold mb-2">Delivery Date</label>
              <input
                type="date"
                id="delivery_date"
                name="delivery_date"
                value={formData.delivery_date}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>

            <div className="mb-6">
              <label htmlFor="details" className="block text-gray-800 font-semibold mb-2">Additional Details (Optional)</label>
              <textarea
                id="details"
                name="details"
                rows="4"
                placeholder="Enter additional instructions"
                value={formData.details}
                onChange={handleInputChange}
                className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-orange-500"
              ></textarea>
            </div>

            <button
              type="submit"
              className="bg-orange-500 text-white font-bold py-3 px-8 rounded-full text-lg inline-flex items-center transition duration-300 ease-in-out hover:bg-orange-600 hover:scale-105 transform"
            >
              Submit Request
              <Package className="ml-2" size={24} />
            </button>
          </form>
        </div>
      </main>
    </div>
  );
}
