import {configureStore } from '@reduxjs/toolkit';
import authReducer from './auth/authSlice';
import darkreduern from './theme/themeSlice'

const store = configureStore({
    reducer:{
        auth:authReducer,
        dark:darkreducer,
    }
})



export default store;
