import { Navigate } from "react-router-dom";
import {jwtDecode} from "jwt-decode";
import api from "../../api";
import { ACCESS_TOKEN } from "../../constants";
import { useState, useEffect } from "react";

function ProtectedRoute({ children }) {
    const [isAuthorized, setIsAuthorized] = useState(null);






    useEffect(() => {
        checkAuth().catch(() => setIsAuthorized(false))
    },[] )

    useEffect(() => {
        const checkAuthStatus = async () => {
            const isAuthenticated = await checkAuth();
            setAuth(isAuthenticated);
        };
        checkAuthStatus();
    }, []); // No dependencies if it runs once


        const checkAuth = async () => {
            try {
                const token = localStorage.getItem(ACCESS_TOKEN);
                if (!token) {
                    setIsAuthorized(false);
                    return;
                }

                const decoded = jwtDecode(token);
                const tokenExpiration = decoded.exp;
                const now = Date.now() / 1000;

                if (tokenExpiration < now) {
                    await refreshToken(); // Uses the secure cookie for refresh
                } else {
                    setIsAuthorized(true);
                }
            } catch (error) {
                console.error("Authentication error:", error);
                setIsAuthorized(false);
            }
        };



    const refreshToken = async () => {
        try {
            const res = await api.post("/api/token/refresh/"); // No need for a refresh token in the body
            if (res.status === 200) {
                localStorage.setItem(ACCESS_TOKEN, res.data.access);
                setIsAuthorized(true);
            } else {
                setIsAuthorized(false);
            }
        } catch (error) {
            console.error("Error refreshing token:", error);
            setIsAuthorized(false);
        }
    };

    if (isAuthorized === null) {
        return <div>Loading...</div>;
    }

    return isAuthorized ? children : <Navigate to="/login" />;
}

export default ProtectedRoute;
