import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import ThemeToggle from './ThemeToggle';

function Navbar() {
  const user = useSelector((state) => state.auth.user);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <header className="bg-white dark:bg-gray-800 shadow-md">
      <nav className="container mx-auto px-6 py-4">
        <div className="flex justify-between items-center">
          <div className="text-2xl font-bold text-orange-500">CommunityGo</div>
          <div className="flex items-center space-x-6">
            <div className="hidden md:flex space-x-6">
              <NavLink to="/" className="text-gray-600 dark:text-gray-300 hover:text-orange-500 dark:hover:text-orange-400 transition duration-300">Home</NavLink>
              <NavLink to="/how-it-works" className="text-gray-600 dark:text-gray-300 hover:text-orange-500 dark:hover:text-orange-400 transition duration-300">How It Works</NavLink>
              <NavLink to="/about" className="text-gray-600 dark:text-gray-300 hover:text-orange-500 dark:hover:text-orange-400 transition duration-300">About Us</NavLink>
              <NavLink to="/contact" className="text-gray-600 dark:text-gray-300 hover:text-orange-500 dark:hover:text-orange-400 transition duration-300">Contact</NavLink>
              {user ? (
                <NavLink to="/profile" className="text-orange-500 hover:underline">{user.username}</NavLink>
              ) : (
                <>
                  <NavLink to="/register" className="text-blue-500 hover:underline">Signup</NavLink>
                  <NavLink to="/login" className="text-blue-500 hover:underline">Login</NavLink>
                </>
              )}
            </div>
            <ThemeToggle darkMode={darkMode} toggleDarkMode={toggleDarkMode} />
          </div>
        </div>
      </nav>
    </header>
  );
}

export default Navbar;
