import {createSlice} from '@reduxjs/toolkit'


const initialState ={

    darkMode:false,
};


const themeSlice = createSlice({
    name:'dark',
    initialState,
    reducers: {
        setdarkMode(state,action){
            state.darkMode = action.payload.dark;
        },

        clearDarkMode
    }
})
