import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';

const DeliveryList = () => {
  const [deliveries, setDeliveries] = useState([]);

  useEffect(() => {
    // You can replace this with an API call to fetch the deliveries from your server
    const fetchDeliveries = async () => {
      try {
        const response = await fetch('/api/deliveries'); // Replace with your API endpoint
        const data = await response.json();
        setDeliveries(data);
      } catch (error) {
        console.error('Error fetching deliveries:', error);
      }
    };

    fetchDeliveries();
  }, []);

  return (
    <div>
      <header className="bg-white shadow-md sticky top-0 z-50">
        <nav className="container mx-auto px-6 py-4">
          <div className="flex justify-between items-center">
            <div className="text-2xl font-bold text-orange-500">CommunityGo</div>
            <div className="hidden md:flex space-x-6">
              <a href="#" className="text-gray-600 hover:text-orange-500 transition duration-300">Home</a>
              <a href="#" className="text-gray-600 hover:text-orange-500 transition duration-300">How It Works</a>
              <a href="#" className="text-gray-600 hover:text-orange-500 transition duration-300">About Us</a>
              <a href="#" className="text-gray-600 hover:text-orange-500 transition duration-300">Contact</a>
              <NavLink to="/login" className="text-blue-500 hover:underline">Login</NavLink>
            </div>
          </div>
        </nav>
      </header>

      <main className="container mx-auto px-6 py-8">
        <h1 className="text-3xl font-bold text-center text-orange-500 mb-8">Requested Deliveries</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {deliveries.map((delivery) => (
            <div key={delivery.id} className="bg-white border rounded-lg shadow-lg p-6">
              <h2 className="text-xl font-semibold text-orange-500 mb-4">Delivery ID: {delivery.id}</h2>
              <p className="text-gray-600"><strong>From:</strong> {delivery.from_address.address_line_1}, {delivery.from_address.city}, {delivery.from_address.state}</p>
              <p className="text-gray-600"><strong>To:</strong> {delivery.to_address.address_line_1}, {delivery.to_address.city}, {delivery.to_address.state}</p>
              <p className="text-gray-600"><strong>Package Size:</strong> {delivery.package_size}</p>
              <p className="text-gray-600"><strong>Status:</strong> {delivery.status}</p>
              <p className="text-gray-600"><strong>Details:</strong> {delivery.details}</p>
              <div className="mt-4">
                <NavLink to={`/deliveries/${delivery.id}`} className="text-blue-500 hover:underline">View Details</NavLink>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default DeliveryList;
