import React , {useState,useEffect } from 'react'
import { useDispatch } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import api from '../api'
import { setAuthData } from '../redux/auth/authSlice'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'


const Login = () => {


  const [email, setEmail ] = useState('')
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [error, setError] = useState();


  const user = useSelector((state) => state.auth.user);


  useEffect(() => {
    if (user) {
      navigate('/home')
    }
  },[user,navigate])




  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/login/', {email, password});
      const {user,token} = response.data;
      localStorage.setItem('user',JSON.stringify(user));
      localStorage.setItem('token',token);
      dispatch(setAuthData(response.data));
      navigate('/home',{replace:true})
    } catch (error) {
      console.error('Login Failed:', error)
      setError('Login faliled.Please check your credentials');
    }
  }
return (
  <div className='flex justify-center items-center h-screen bg-gray-100'>
  <div className='bg-white p-8 rounded-lg shadow-lg w-96'>
    <div className='flex justify-center mb-6'>
      <div className='bg-gray-200 rounded-full w-16 h-16 flex justify-center items-center'>
        <h2 className='text-xl font-semibold text-gray-700'>User Login</h2>
      </div>
    </div>
    <form onSubmit={handleLogin} className='space-y-4'>
      <div>
        <input
          type="text"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder='Email'
          required
          className='w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500'
        />
      </div>
      <div>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
          className='w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500'
        />
        <span className='absolute top-1/2 right-3 transform -translate-y-1/2 cursor-pointer'></span>
      </div>
      <button
        type='submit'
        className='w-full bg-blue-600 text-white p-3 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500'
      >
        LOGIN
      </button>
    </form>
    {error && <div className='mt-4 text-red-600 text-center'>{error}</div>}
    <div className='mt-4 text-center'>
      <p className='text-sm'>
        Don't have an account? <Link to="/signup" className='text-blue-600 hover:underline'>Signup</Link>
      </p>
      <p className='text-sm'>
        Switch to Admin? <Link to="/admin/login" className='text-blue-600 hover:underline'>Admin-Login</Link>
      </p>
    </div>
  </div>
</div>

)


}


export default Login
