import React , {useState,useEffect } from 'react'
import { useDispatch } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import api from '../api'
import { setAuthData } from '../redux/auth/authSlice'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'


const Login = () => {


  const [email, setEmail ] = useState('')
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [error, setError] = useState();
  const [showPassword,setShowPassword] = useState(false);


  const user = useSelector((state) => state.auth.user);


  useEffect(() => {
    if (user) {
      navigate('/home')
    }
  },[user,navigate])

  const togglePasswordVisibility = () => {
    setShowPassword(prev)
  }


  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/login/', {email, password});
      const {user,token} = response.data;
      localStorage.setItem('user',JSON.stringify(user));
      localStorage.setItem('token',token);
      dispatch(setAuthData(response.data));
      navigate('/home',{replace:true})
    } catch (error) {
      console.error('Login Failed:', error)
      setError('Login faliled.Please check your credentials');
    }
  }
return (
  <div className="flex justify-center items-center min-h-screen bg-gray-100">
    <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <div className="flex justify-center mb-6">
            <div className="w-16 h-16 rounded-full bg-blue-500 flex justify-center items-center text-white text-xl">
                <h2>User Login</h2>
            </div>
        </div>
        <form onSubmit={handleLogin}>
            <div className="mb-4">
                <input 
                    type="text" 
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                    required
                    className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
            </div>
            <div className="mb-6 relative">
                <input 
                    type="password" 
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    required
                    className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <span className="absolute top-2 right-2 cursor-pointer text-gray-500">üëÅÔ∏è</span>
            </div>
            <button 
                type="submit" 
                className="w-full py-2 bg-blue-500 text-white font-semibold rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
                LOGIN
            </button>
        </form>
        {error && <div className="mt-4 text-red-500 text-center">{error}</div>}
        <div className="mt-6 text-center">
            <p className="text-sm text-gray-600">
                Don't have an account? 
                <Link to="/signup" className="text-blue-500 hover:underline">Signup</Link>
                <br />
                Switch to Admin? 
                <Link to="/admin/login" className="text-blue-500 hover:underline">Admin-Login</Link>
            </p>
        </div>
    </div>
</div>

)


}


export default Login
