import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react'

import api from '../../api'

export default function Profile() {
  // In a real application, you would fetch this data from an API or database
  const [user, setUser] = useState(null);
  const [error, setError] = useState();

  useEffect(() => {
    const fetchprofile = async () => {

      console.log('hello');
      try {
        const response = await api.get(`/profile`)
        console.log('response',response)
        if (response.data.success) {
          setUser(response.data.user)
          console.log(user)
        } else {
          setError('error occured while fetching user profile')
        }
      } catch (error) {
        console.log('An unexpected errror occured');
      }
    };

    fetchprofile()
  }, []);



  if (!user) {
    return (
      <div className="p-6">
        <p>Loading...</p>
        console
      </div>
    ); // Show loading message if user data is null
  }


  return (
    <div className="container mx-auto py-10 px-4">
      <div className="max-w-4xl mx-auto bg-gray-100 dark:bg-gray-800 rounded-lg overflow-hidden shadow-lg">
        <div className="bg-orange-500 text-white py-6">
          <h1 className="text-2xl font-bold text-center">User Profile</h1>
        </div>
        <div className="p-6">
          <div className="flex flex-col md:flex-row gap-6">
            <div className="flex flex-col items-center">
              <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-orange-500 mb-4">
                <img src="/placeholder.svg?height=128&width=128" alt="User Avatar" className="w-full h-full object-cover" />
              </div>
            </div>
            <div className="flex-grow">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-300">{user.username}</h2>
                  <p className="text-gray-600 dark:text-gray-400">{user.name}</p>
                </div>
                <div>
                  <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-300">{user.email}</h2>
                  <p className="text-gray-600 dark:text-gray-400">{user.email}</p>
                </div>
                {/* <div className="md:col-span-2">
                  <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-300">{user.first_name}</h2>
                  <p className="text-gray-600 dark:text-gray-400">{user.bio}</p>
                </div>
                <div>
                  <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-300">Location</h2>
                  <p className="text-gray-600 dark:text-gray-400">{user.location}</p>
                </div>
                <div>
                  <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-300">Website</h2>
                  <a href={user.website} className="text-orange-500 hover:underline" target="_blank" rel="noopener noreferrer">{user.website}</a>
                </div>
                <div>
                  <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-300">Twitter</h2>
                  <a href={`https://twitter.com/${user.twitter.replace('@', '')}`} className="text-orange-500 hover:underline" target="_blank" rel="noopener noreferrer">{user.twitter}</a>
                </div>
                <div>
                  <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-300">GitHub</h2>
                  <a href={`https://github.com/${user.github}`} className="text-orange-500 hover:underline" target="_blank" rel="noopener noreferrer">{user.github}</a>
                </div> */}
              </div>
            </div>
          </div>
        </div>
        <div className="px-6 py-4 bg-gray-200 dark:bg-gray-700">
          <Link href="/profile/edit" className="block w-full py-2 px-4 bg-orange-500 text-white text-center rounded-md hover:bg-orange-600 transition-colors">
            Edit Profile
          </Link>
        </div>
      </div>
    </div>
  )
}
